import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from '../Components/Navvbar';
import styles from './SongInfo.module.css';
import axios from 'axios';

const SongInfo = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [lyrics, setLyrics] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const { title, author, genre, img } = location.state || {};

    useEffect(() => {
        const fetchLyrics = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`https://api.lyrics.ovh/v1/${author}/${title}`);
                setLyrics(response.data.lyrics);
                setError(null);
            } catch (err) {
                setError('Failed to fetch lyrics. Please try again later.');
                console.error('Error fetching lyrics:', err);
            } finally {
                setLoading(false);
            }
        };

        if (title && author) {
            fetchLyrics();
        }
    }, [title, author]);

    if (!location.state) {
        return (
            <div>
                <Navbar />
                <div className={styles.error}>Song information not found</div>
            </div>
        );
    }

    return (
        <div>
            <Navbar />
            <div className={styles.container}>
                <div className={styles.songHeader}>
                    <img src={img} alt={title} className={styles.albumArt} />
                    <div className={styles.songDetails}>
                        <h1 className={styles.title}>{title}</h1>
                        <h2 className={styles.artist}>{author}</h2>
                        <p className={styles.genre}>{genre}</p>
                    </div>
                </div>

                <div className={styles.lyricsSection}>
                    <h2>Lyrics</h2>
                    {loading ? (
                        <div className={styles.loading}>Loading lyrics...</div>
                    ) : error ? (
                        <div className={styles.error}>{error}</div>
                    ) : (
                        <div className={styles.lyrics}>
                            {lyrics.split('\n').map((line, index) => (
                                <p key={index}>{line}</p>
                            ))}
                        </div>
                    )}
                </div>
                <div className={styles.additional}>
                    <div></div>
                </div>
            </div>
        </div>
    );
};

export default SongInfo;
