import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Navbar from '../Components/Navvbar';
import Annotation from '../Components/Annotation';
import styles from './SongInfo.module.css';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const SongInfo = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [lyrics, setLyrics] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [annotations, setAnnotations] = useState([]);
    const [newAnnotation, setNewAnnotation] = useState('');
    const [selectedTimestamp, setSelectedTimestamp] = useState('');
    const { user, isLoggedIn } = useAuth();
    const [aiInput, setAiInput] = useState('');
    const [aiAnswer, setAiAnswer] = useState('');
    const [aiLoading, setAiLoading] = useState(false);
    const [aiError, setAiError] = useState('');

    const { title, author, genre, img, text } = location.state || {};

    useEffect(() => {
        if (text) {
            setLyrics(text);
            setLoading(false);
        } else if (title && author) {
            const fetchLyrics = async () => {
                try {
                    setLoading(true);
                    const response = await axios.get(`https://api.lyrics.ovh/v1/${author}/${title}`);
                    setLyrics(response.data.lyrics);
                    setError(null);
                } catch (err) {
                    setError('Failed to fetch lyrics. Please try again later.');
                    console.error('Error fetching lyrics:', err);
                } finally {
                    setLoading(false);
                }
            };
            fetchLyrics();
        }

        const fetchAnnotations = async () => {
            try {
                const response = await axios.get(`http://localhost:3001/api/annotations/${title}/${author}`);
                setAnnotations(response.data);
            } catch (err) {
                console.error('Error fetching annotations:', err);
            }
        };

        if (title && author) {
            fetchAnnotations();
        }
    }, [title, author, text]);

    const handleAddAnnotation = async (e) => {
        e.preventDefault();
        if (!newAnnotation.trim() || !selectedTimestamp || !isLoggedIn) return;

        try {
            const response = await axios.post('http://localhost:3001/api/annotations', {
                songTitle: title,
                songArtist: author,
                timestamp: selectedTimestamp,
                text: newAnnotation,
                userId: user._id
            });

            setAnnotations([...annotations, response.data]);
            setNewAnnotation('');
            setSelectedTimestamp('');
        } catch (err) {
            console.error('Error adding annotation:', err);
            alert('Failed to add annotation. Please try again.');
        }
    };

    const handleDeleteAnnotation = async (id) => {
        try {
            await axios.delete(`http://localhost:3001/api/annotations/${id}`, {
                data: { userId: user._id }
            });
            setAnnotations(annotations.filter(annotation => annotation._id !== id));
        } catch (err) {
            console.error('Error deleting annotation:', err);
            alert('Failed to delete annotation. Please try again.');
        }
    };

    if (!location.state) {
        return (
            <div>
                <Navbar />
                <div className={styles.error}>Song information not found</div>
            </div>
        );
    }

    return (
        <div>
            <Navbar />
            <div className={styles.container}>
                <div className={styles.songHeader}>
                    <img src={img} alt={title} className={styles.albumArt} />
                    <div className={styles.songDetails}>
                        <h1 className={styles.title}>{title}</h1>
                        <h2 className={styles.artist}>{author}</h2>
                        <p className={styles.genre}>{genre}</p>
                    </div>
                </div>

                <div className={styles.contentWrapper}>
                    <div className={styles.lyricsSection}>
                        <h2>Lyrics</h2>
                        {loading ? (
                            <div className={styles.loading}>Loading lyrics...</div>
                        ) : error ? (
                            <div className={styles.error}>{error}</div>
                        ) : (
                            <div className={styles.lyrics}>
                                {lyrics.split('\n').map((line, index) => (
                                    <p key={index}>{line}</p>
                                ))}
                            </div>
                        )}
                        {/* AI Annotation Section */}
                        <div style={{ marginTop: 32, background: '#f8f9fa', padding: 16, borderRadius: 12 }}>
                          <h3>Ask AI for Annotation</h3>
                          <input
                            type="text"
                            value={aiInput}
                            onChange={e => setAiInput(e.target.value)}
                            placeholder="Enter part of the song or a question"
                            style={{ width: '60%', marginRight: 8 }}
                          />
                          <button
                            onClick={async () => {
                              setAiLoading(true);
                              setAiError('');
                              setAiAnswer('');
                              try {
                                const res = await axios.post('http://localhost:3001/api/ai/ask-annotation', {
                                  question: aiInput,
                                  song: title,
                                  author: author
                                });
                                setAiAnswer(res.data.answer);
                              } catch (err) {
                                setAiError('Failed to get AI annotation.');
                              } finally {
                                setAiLoading(false);
                              }
                            }}
                            disabled={aiLoading || !aiInput}
                            style={{ height: 40 }}
                          >
                            {aiLoading ? 'Asking...' : 'Ask AI Annotation'}
                          </button>
                          {aiError && <div style={{ color: 'red', marginTop: 8 }}>{aiError}</div>}
                          {aiAnswer && <div style={{ marginTop: 12, background: '#fff', padding: 12, borderRadius: 8 }}>{aiAnswer}</div>}
                        </div>
                    </div>

                    <div className={styles.annotationsSection}>
                        <h2>Annotations</h2>
                        {isLoggedIn ? (
                            <form onSubmit={handleAddAnnotation} className={styles.annotationForm}>
                                <input
                                    type="text"
                                    placeholder="Enter timestamp (e.g., 0:45)"
                                    value={selectedTimestamp}
                                    onChange={(e) => setSelectedTimestamp(e.target.value)}
                                    className={styles.timestampInput}
                                />
                                <textarea
                                    placeholder="Write your annotation..."
                                    value={newAnnotation}
                                    onChange={(e) => setNewAnnotation(e.target.value)}
                                    className={styles.annotationInput}
                                />
                                <button type="submit" className={styles.addButton}>
                                    Add Annotation
                                </button>
                            </form>
                        ) : (
                            <p className={styles.loginPrompt}>
                                Please log in to add annotations
                            </p>
                        )}

                        <div className={styles.annotationsList}>
                            {annotations.map(annotation => (
                                <Annotation
                                    key={annotation._id}
                                    annotation={annotation}
                                    onDelete={handleDeleteAnnotation}
                                    currentUserId={user?._id}
                                />
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default SongInfo;
