import React, { useState } from 'react'
import styles from './AddSong.module.css'
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const AddSong = ({ onClose, onSongAdded }) => {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: '',
    lyrics: '',
    image: null
  });
  const [imagePreview, setImagePreview] = useState(null);
  const [error, setError] = useState('');
  const { user } = useAuth();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        image: file
      }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const songData = {
        title: formData.title,
        author: formData.author,
        genre: formData.genre,
        lyrics: formData.lyrics,
        image: imagePreview,
        userId: user._id
      };

      await axios.post('http://localhost:3001/api/songs', songData);
      onSongAdded(); 
    } catch (err) {
      setError('Failed to add song. Please try again.');
      console.error('Error adding song:', err);
    }
  };

  return (
    <div className={styles.overlay}>
      <div className={styles.popup}>
        <button className={styles.closebtn} onClick={onClose}>×</button>
        <h1 className={styles.title}>Add New Song</h1>
        {error && <div className={styles.error}>{error}</div>}
        <form className={styles.form} onSubmit={handleSubmit}>
          <div className={styles.inputGroup}>
            <input
              className={styles.input}
              type="text"
              name="title"
              placeholder="Song Title"
              value={formData.title}
              onChange={handleInputChange}
              required
            />
            <input
              className={styles.input}
              type="text"
              name="author"
              placeholder="Artist Name"
              value={formData.author}
              onChange={handleInputChange}
              required
            />
            <input
              className={styles.input}
              type="text"
              name="genre"
              placeholder="Genre"
              value={formData.genre}
              onChange={handleInputChange}
              required
            />
          </div>

          <div className={styles.imageUpload}>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              style={{ display: 'none' }}
              id="imageInput"
            />
            <label htmlFor="imageInput">
              {imagePreview ? (
                <img src={imagePreview} alt="Preview" className={styles.imagePreview} />
              ) : (
                <div>
                  <p>Click to upload song cover image</p>
                  <p style={{ fontSize: '12px', color: '#666' }}>Recommended size: 500x500px</p>
                </div>
              )}
            </label>
          </div>

          <textarea
            className={styles.textarea}
            name="lyrics"
            placeholder="Enter song lyrics..."
            value={formData.lyrics}
            onChange={handleInputChange}
            required
          />

          <button type="submit" className={styles.submitBtn}>
            Add Song
          </button>
        </form>
      </div>
    </div>
  )
}

export default AddSong;
